import React from 'react';
import Sidebar from '../components/Sidebar.js';
import PreNavbar from '../components/PreNavbar.js';
import City from '../components/City.js';
import { connect } from "react-redux";
import * as actionCreator from '../store/actions/actions.js';

class CitiesPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {cityFilter: "", filteredCities: []};
    }

    handleFilterInput = (poetFilter) => {
        let filteredCities = this.state.poets

        filteredCities = filteredCities.filter((poet) => {
            let poetName = poet.firstName.toLowerCase() + poet.lastName.toLowerCase()
            return poetName.indexOf(poetFilter.toLowerCase()) !== -1  
        })

        this.setState({filteredCities})
    }

    handleFilterInput = (poetFilter) => {
        let filteredPoets = this.state.poets

        filteredPoets = filteredPoets.filter((poet) => {
            let poetName = poet.firstName.toLowerCase() + poet.lastName.toLowerCase()
            return poetName.indexOf(poetFilter.toLowerCase()) !== -1  
        })

        this.setState({filteredPoets})
    }

    handleChange = (e) => {
        this.setState({cityFilter: e.target.value});
    }

    componentDidMount() {
        this.props.getCities();
    }

    render() {
        if (this.props.citiesIsLoading) {
            return <div>Loading...</div>;
        } else {
            return (
                <div>
                    <Sidebar />
                    <PreNavbar />
                    <div>
                        <h1 id="header" className="cityTitle">Cities</h1>
                        <input value={this.state.cityFilter} onChange={this.handleChange} className="cityFilter" type="text" placeholder="Search a city" name="Search city"></input>
                        {
                            this.props.cities.map((item, index) => (
                                <City name={item.name} image={item.image} key={index} />
                            ))
                        }
                    </div>
                </div>
            ); 
        }
    }
}

const mapStateToProps = (state) => {
    return {cities: state.cities, city: state.city, citiesIsLoading: state.citiesIsLoading}
}

function mapDispatchToProps(dispatch) {
    return {
        getCities: () => dispatch(actionCreator.fetchCitiesData()),
    }
}

export default connect(mapStateToProps, mapDispatchToProps) (CitiesPage);
