var i = false;

export const getActivities = (activities) => {
    if(!i) {
        return {
            type: 'GET_ACTIVITIES',
            activities
        }
        i = trueM
    }
}

export const activitiesIsLoading = (activitiesIsLoading) => {
    return {
        type: 'ACTIVITIES_IS_LOADING',
        activitiesIsLoading
    }
}

export const fetchActivitiesData = (uuid) => {
    return (dispatch) => {
        fetch("/cities/activities/" + uuid)
            .then(response => response.json())
            .then(result => {
                dispatch(getActivities(result));
                dispatch(activitiesIsLoading(false));
            })
            .catch(e => console.log(e));
    }
}
