import React from 'react';
import City from './City.js';
import data from '../assets/json/data.js';

class SecondContent extends React.Component {
    constructor() {
        super();
        this.cityOne = null;
        this.cityTwo = null;
        this.cityThree = null;
        this.cityFour = null;
    }

    createTable() {
        var table = [];

        this.fillTable(table);
        console.log("Table-Length: " + table.length);

        for(let i = 0; i < data.length; i++) {
            console.log(data[i].name + " : " + data[i].used);
        }

        if (table.length === 4) {
            return table;
        } else {
            console.log("error");
            return table;
        }
    }

    setCities(table) {
        var index = 0;
        do { 
            table.push(this.getRandomCity());
            index++;
        } while (index < 4);
    }

    fillTable(table) {
        for (let i = 0; i < 4; i++) {
            const city = this.getRandomCity();
            if (!city.used) {
                city.used = true;
                table.push(
                    <li key={i}> <City src={city.image} name={city.name} /> </li>
                );
            }
        }
    }

    getRandomCity() {
        const current = data[Math.floor(Math.random() * data.length)];
        return current;
    }

    render() {
        return (
            <div className="content2">
                <h5 className="city_header">Popular MYtineraries</h5>
                <ul className="cities">
                    {this.createTable()}
                </ul>
            </div>
        );
    }
}

export default SecondContent;
