import React from 'react';

class Itinery extends React.Component {

    constructor(props) {
        super(props);
        this.profilePic = {profilePic: props.profilePic};
        this.username = {username: props.username};
        this.title = {title: props.title};
        this.rating = {rating: props.rating};
        this.duration = {duration: props.duration};
        this.hashtag = {hashtag: props.hashtag};
        this.uuid = {uuid: props.uuid};
    }

    getActivitiesForItinery(uuid) {
        if (!this.bool) {
            this.activities = this.props.activities;
            var array = [];
            for (var i = 0; i < this.activities.length; i++) {
                if (this.activities[i].uuid === uuid) {
                    array.push(this.activities[i]);
                }
            }
            this.activitiesForItinery = array;
            this.bool = true;            
        } else {
            this.activitiesForItinery = null;
            this.bool = false;
        }
    }

    expand(element) {
        var parent = element.parentNode;
        if (!parent.hasAttribute("openend")) {
            parent.setAttribute("openend", true);
            for (var i = 0; i < document.getElementsByClassName("itinery").length; i++) {
                if (!document.getElementsByClassName("itinery")[i].hasAttribute("openend")) {
                    document.getElementsByClassName("itinery")[i].style.display = "none";
                }
            }
            parent.scrollIntoView(true);
            parent.classList.add("open"); 
        } else {
            for (var c = 0; c < document.getElementsByClassName("itinery").length; c++) {
                if (!document.getElementsByClassName("itinery")[c].hasAttribute("openend")) {
                    document.getElementsByClassName("itinery")[c].style.display = "block";
                }
            }
            parent.removeAttribute("openend");
            parent.classList.remove("open");
        }
    }

    render() {
        return (
            <div className="itinery">
                <div className="profile">
                    <img className="profileImage" src={this.profilePic === "" ? "https://immedilet-invest.com/wp-content/uploads/2016/01/user-placeholder.jpg" : this.profilePic} alt="profile wallpaper"></img>
                    <p>{this.username}</p>
                </div>
                <h1 className="itineryStatus">{this.title}</h1>
                <i id="expander" onClick={(event) => { this.expand(event.target); this.getActivitiesForItinery(this.uuid)}} className="fa fa-expand-arrows-alt expand"></i>
                <ul>
                    <li className="likes"> Likes: {this.rating} </li>
                    <li className="hours"> {this.duration} Hours </li>
                </ul>
                <ul className="review">
                    {this.hashtag.map((item, index) => (
                        <li key={index}>{item}</li>
                    ))}
                </ul>
            </div>
        );
    }
}

export default Itinery;
