const initState = {
    cities: [],
    city: [],
    activities: []
};

export const rootReducer = (state = initState, action) => {

    switch (action.type) {
        case 'GET_ACTIVITIES':
            state = {
                ...state,
                activities: action.activities
            }   
            break;
        case 'ACTIVITIES_IS_LOADING':
            state = {
                ...state,
                activitiesIsLoading: action.activitiesIsLoading
            }
            break;
        case 'GET_CITIES':
            state = {
                ...state,
                cities: action.cities
            }
            break;
        case 'CITIES_IS_LOADING':
            state = {
                ...state,
                citiesIsLoading: action.citiesIsLoading
            }
            break;
        case 'GET_CITY':
            state = {
                ...state,
                city: action.city.message
            }
            break;
        case 'CITY_IS_LOADING':
            state = {
                ...state,
                cityIsLoading: action.cityIsLoading
            }
            break;
        default:
            break;
    }

    return state;
}
