import React from 'react';
import City from './City.js';
import data from '../assets/json/data.js';

class SecondContent extends React.Component {
    createTable() {
        var table = [];

        for (let i = 0; i < data.length; i++) {
            if(i <= 3) {
                const rand = Math.round(Math.random() * data.length);
                var image = null;
                var name = null;

                if (!data[rand].used) {
                    if(rand < 10) {
                        image = data[rand].image;
                        name = data[rand].name;
                        data[rand].used = true;
                    } else {
                        image = data[rand - 1].image;
                        name = data[rand - 1].name;
                        data[rand].used = true;
                    }
                }

                if (image !== null && name !== null) {
                    table.push(
                        <li key={i}>
                            <City src={image} name={name} />
                        </li>
                    );
                }

            } else {
                break;
            }
        }
        return table;
    }

    render() {
        return (
            <div className="content2">
                <h5 className="city_header">Popular MYtineraries</h5>
                <ul className="cities">
                    {this.createTable()}
                </ul>
            </div>
        );
    }
}

export default SecondContent;
